import React, { useState, useContext } from "react";
import {
  Box,
  TextField,
  Typography,
  List,
  ListItem,
  ListItemText,
  Button,
  IconButton,
  Tooltip,
  Grow,
  Divider,
  Stack,
  useMediaQuery,
  useTheme
} from "@mui/material";
import {
  BarChartOutlined,
  KeyboardArrowDown,
  KeyboardArrowUp,
  MoreHoriz,
} from "@mui/icons-material";

// Import context from the correct location
import GeneralContext from "../context/GeneralContext";

// Sample data if you don't have the actual data
const watchList = [
  { name: "RELIANCE", price: "2,456.75", percent: "+1.2%", isDown: false },
  { name: "TATASTEEL", price: "1,245.60", percent: "-0.8%", isDown: true },
  { name: "INFY", price: "1,678.30", percent: "+2.1%", isDown: false },
  { name: "HDFCBANK", price: "1,567.45", percent: "+0.5%", isDown: false },
  { name: "TCS", price: "3,456.20", percent: "-0.3%", isDown: true }
];

const WatchList = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  return (
    <Box sx={{ 
      height: "100%", 
      display: "flex", 
      flexDirection: "column",
      p: { xs: 1, sm: 1, md: 2 }
    }}>
      <Box sx={{ position: "relative", mb: 1 }}>
        <TextField
          fullWidth
          size="small"
          placeholder={isMobile ? "Search stocks..." : "Search eg:infy, bse, nifty fut weekly, gold mcx"}
          variant="outlined"
          aria-label="Search stocks"
          InputProps={{
            endAdornment: (
              <Typography variant="caption" color="text.secondary">
                {watchList.length} / 50
              </Typography>
            ),
          }}
        />
      </Box>

      <List sx={{ 
        flexGrow: 1, 
        overflow: "auto", 
        p: 0,
        '& .MuiListItem-root': {
          px: { xs: 1, sm: 2 },
          py: { xs: 0.5, sm: 1 }
        }
      }}>
        {watchList.map((stock, index) => (
          <React.Fragment key={`${stock.name}-${index}`}>
            <WatchListItem stock={stock} />
            {index < watchList.length - 1 && <Divider />}
          </React.Fragment>
        ))}
      </List>
    </Box>
  );
};

const WatchListItem = ({ stock }) => {
  const [showWatchListActions, setShowWatchListActions] = useState(false);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const handleMouseEnter = () => {
    if (!isMobile) {
      setShowWatchListActions(true);
    }
  };

  const handleMouseLeave = () => {
    if (!isMobile) {
      setShowWatchListActions(false);
    }
  };

  const handleClick = () => {
    if (isMobile) {
      setShowWatchListActions(!showWatchListActions);
    }
  };

  return (
    <ListItem
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      onClick={handleClick}
      aria-label={`Stock: ${stock.name}`}
      sx={{ 
        position: "relative",
        cursor: isMobile ? 'pointer' : 'default'
      }}
    >
      <ListItemText
        primary={
          <Typography 
            variant="body2" 
            color={stock.isDown ? "error.main" : "success.main"}
            sx={{ fontSize: { xs: '0.8rem', sm: '0.875rem' } }}
          >
            {stock.name}
          </Typography>
        }
        secondary={
          <Stack 
            direction="row" 
            spacing={1} 
            alignItems="center"
            sx={{ mt: 0.5 }}
          >
            <Typography 
              variant="caption" 
              color="text.secondary"
              sx={{ fontSize: { xs: '0.7rem', sm: '0.75rem' } }}
            >
              {stock.percent}
            </Typography>
            {stock.isDown ? (
              <KeyboardArrowDown fontSize="small" color="error" aria-label="Price down" />
            ) : (
              <KeyboardArrowUp fontSize="small" color="success" aria-label="Price up" />
            )}
            <Typography 
              variant="body2"
              sx={{ fontSize: { xs: '0.75rem', sm: '0.875rem' } }}
            >
              {stock.price}
            </Typography>
          </Stack>
        }
      />

      {showWatchListActions && <WatchListActions uid={stock.name} isMobile={isMobile} />}
    </ListItem>
  );
};

const WatchListActions = ({ uid, isMobile }) => {
  const generalContext = useContext(GeneralContext);

  const handleBuyClick = () => {
    if (generalContext && generalContext.openBuyWindow) {
      generalContext.openBuyWindow(uid);
    } else {
      console.log("Buy clicked for", uid);
    }
  };

  return (
    <Box 
      sx={{ 
        position: isMobile ? "static" : "absolute", 
        right: isMobile ? 'auto' : 8, 
        display: "flex", 
        alignItems: "center",
        justifyContent: isMobile ? "flex-end" : "flex-start",
        flexWrap: "wrap",
        mt: isMobile ? 1 : 0,
        gap: 0.5
      }}
    >
      <Tooltip title="Buy (B)" placement="top" arrow TransitionComponent={Grow}>
        <Button 
          size="small" 
          variant="contained" 
          color="primary" 
          onClick={handleBuyClick} 
          aria-label={`Buy stock ${uid}`}
          sx={{ 
            minWidth: { xs: "30px", sm: "40px" },
            fontSize: { xs: '0.7rem', sm: '0.8rem' }
          }}
        >
          Buy
        </Button>
      </Tooltip>

      <Tooltip title="Sell (S)" placement="top" arrow TransitionComponent={Grow}>
        <Button 
          size="small" 
          variant="contained" 
          color="error" 
          aria-label={`Sell stock ${uid}`}
          sx={{ 
            minWidth: { xs: "30px", sm: "40px" },
            fontSize: { xs: '0.7rem', sm: '0.8rem' }
          }}
        >
          Sell
        </Button>
      </Tooltip>

      <Tooltip title="Analytics (A)" placement="top" arrow TransitionComponent={Grow}>
        <IconButton 
          size="small" 
          aria-label={`View analytics for ${uid}`}
        >
          <BarChartOutlined fontSize="small" />
        </IconButton>
      </Tooltip>

      <Tooltip title="More" placement="top" arrow TransitionComponent={Grow}>
        <IconButton 
          size="small" 
          aria-label={`More options for ${uid}`}
        >
          <MoreHoriz fontSize="small" />
        </IconButton>
      </Tooltip>
    </Box>
  );
};

export default WatchList;
